        intro: o.description.replace(/<\/?\w+>/g, '').trim(),
        cover: o.coverImage,
        catalogUrl: '/reader/book/info.json?catalog_only=true&source_uuid=' + o.sourceUuid
    }
});

ruleContent.content:
ChapterContent@js:
function p(e) {
    return e.split("").map(function (e) {
        return e.match(/[A-Za-z]/) ? (c = Math.floor(e.charCodeAt(0) / 97), k = (e.toLowerCase().charCodeAt(0) - 83) % 26 || 26, String.fromCharCode(k + (0 == c ? 64 : 96))) : e
    }).join("")
}
java.base64Decode(p(result))

