ruleToc.chapterList:
//ol/*<js>
//按卷分隔
sub=String(result).slice(1,-1).split(/, (?=<li class="chapter-bar chapter-li")/);
er=0;
sm=java.getString("h1@text");
//显示卷名
var opts = {
  showVolName: false,
  showVol:true
};
if(sub.length>=1)for(var i=0;i<sub.length;i++){
//捕获卷名
jm=sub[i].match(/\s*([^<>\n]+)\s*<\/li/);
if(!jm)continue;

//插入卷名
sub[i]=sub[i]
.replace(/html([^>]*)><span([^>]*)>/g,(opts.showVolName ? 'html$1><span$2>★'+jm[1]+'★' :'html$1><span$2>'+ ('').padStart(3, "\u2000")))

.replace(/javascript\:cid\(0\)([^>]*)><span([^>]*)>/g,(opts.showVolName ? 'javascript:cid(0)\\'+jm[1]+'$1><span$2>['+jm[1]+']❗' :'javascript:cid(0)\\'+jm[1]+'$1><span$2>'+ ('').padStart(3, "\u2000")));

if(opts.showVol)
//按卷分块
sub[i]='<a href="https://translate.google.cn/#view=home&op=translate&sl=zh-CN&tl=en&text='+sm+'\\'+jm[1]+'">★'+jm[1]+'★</a>'+sub[i]
};
//合并结果
sub.join("");

//检查灰色章节
sub_gray=String(sub).split(/(?=<a href="javascript\:cid\(0\))/);

for(var j=0;j<sub_gray.length;j++){
sub_gray[j]=sub_gray[j]
.replace(/href="javascript\:cid\(0\)([^"]*)"([^>]*)><span([^>]*)>(\s*)([^<]*)/g,'href="https://translate.google.cn/#view=home&op=translate&sl=zh-CN&tl=en&text='+sm+'$1'+'\\'+"灰色章节："+(er++)+'"$2><span$3>$4❗$5❗');
}

sub_gray.join("");

</js>a

